##     Orserver#require 'set'#############################class Notifier  def initialize    @listener=Set.new  end  def notify    @listener.collect { |l| l.occur() }  end  def add_listener(l)    @listener.add(l)  endend##############################class Listener  def occur()    puts "not implemented!!!"  endend##############################class CCC < Notifier  def notif    notify()  endendclass BBB < Listener # not necessarily (duck typing)  def occur()    puts "overrided occur"  endendccc=CCC.newbbb=BBB.newccc.add_listener(bbb)ccc.notif()